"use client";

import { motion, useReducedMotion } from "framer-motion";

export interface IconAlignHorizontalSpaceBetweenProps extends React.SVGProps<SVGSVGElement> {
  /** Size in pixels. Default 24 */
  size?: number;
  /** Stroke width. Default 2 */
  strokeWidth?: number;
}

const leftVariants = {
  rest: { x: 0 },
  hover: { x: -1.5 },
  tap: { x: 1 },
};

const rightVariants = {
  rest: { x: 0 },
  hover: { x: 1.5 },
  tap: { x: -1 },
};

const guideVariants = {
  rest: { opacity: 0.6 },
  hover: { opacity: 1 },
  tap: { opacity: 0.5 },
};

export function IconAlignHorizontalSpaceBetween({
  size = 24,
  strokeWidth = 2,
  className,
  ...props
}: IconAlignHorizontalSpaceBetweenProps) {
  const {
    onAnimationStart,
    onAnimationEnd,
    onDragStart,
    onDrag,
    onDragEnd,
    ...rest
  } = props;
  const prefersReducedMotion = useReducedMotion();
  return (
    <motion.svg
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth={strokeWidth}
      strokeLinecap="round"
      strokeLinejoin="round"
      initial={prefersReducedMotion ? false : "rest"}
      whileHover={prefersReducedMotion ? undefined : "hover"}
      whileTap={prefersReducedMotion ? undefined : "tap"}
      transition={{ type: "spring", stiffness: 320, damping: 18 }}
      className={`outline-none focus:outline-none focus:ring-0 select-none ${className ?? ""}`.trim()}
      {...rest}
    >
      <motion.rect width="6" height="14" x="3" y="5" rx="2" variants={leftVariants} />
      <motion.rect width="6" height="10" x="15" y="7" rx="2" variants={rightVariants} />
      <motion.path d="M3 2v20" variants={guideVariants} />
      <motion.path d="M21 2v20" variants={guideVariants} />
    </motion.svg>
  );
}
