"use client";

import { motion, useReducedMotion } from "framer-motion";

export interface IconArrowDownUpProps extends React.SVGProps<SVGSVGElement> {
  /** Size in pixels. Default 24 */
  size?: number;
  /** Stroke width. Default 2 */
  strokeWidth?: number;
}

const downVariants = {
  rest: { y: 0 },
  hover: { y: 2 },
  tap: { y: 4 },
};

const upVariants = {
  rest: { y: 0 },
  hover: { y: -2 },
  tap: { y: -4 },
};

export function IconArrowDownUp({ size = 24, strokeWidth = 2, className, ...props }: IconArrowDownUpProps) {
  const {
    onAnimationStart,
    onAnimationEnd,
    onDragStart,
    onDrag,
    onDragEnd,
    ...rest
  } = props;
  const prefersReducedMotion = useReducedMotion();
  return (
    <motion.svg
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth={strokeWidth}
      strokeLinecap="round"
      strokeLinejoin="round"
      initial={prefersReducedMotion ? false : "rest"}
      whileHover={prefersReducedMotion ? undefined : "hover"}
      whileTap={prefersReducedMotion ? undefined : "tap"}
      transition={{ type: "spring", stiffness: 320, damping: 18 }}
      className={`outline-none focus:outline-none focus:ring-0 select-none ${className ?? ""}`.trim()}
      {...rest}
    >
      <motion.g variants={downVariants}>
        <path d="m3 16 4 4 4-4" />
        <path d="M7 20V4" />
      </motion.g>
      <motion.g variants={upVariants}>
        <path d="m21 8-4-4-4 4" />
        <path d="M17 4v16" />
      </motion.g>
    </motion.svg>
  );
}
